<div id="modalPDF" class="modal" tabindex="-1" role="dialog" style="display:none;">
    <div class="modal-dialog modal-lg" role="document">
        <div class="modal-content">
            <div class="modal-header">
                <h5 class="modal-title">Vista Previa del PDF</h5>
                <button type="button" class="close" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>
            <div class="modal-body">
                <iframe id="pdfViewer" src="" style="width: 100%; height: 500px;" frameborder="0"></iframe>
            </div>
            <div class="modal-footer">
                <button id="downloadPdf" class="btn btn-primary">Descargar</button>
                <button id="printPdf" class="btn btn-success">Imprimir</button>
                <button type="button" class="btn btn-secondary" id="closeModalImprimir">Cerrar</button>
                
            </div>
        </div>
    </div>
</div>
